import React from "react"
import { useDisclosure } from "@chakra-ui/react"

export default function PopOver({ ref, ...props }) {
    const outsideRef = React.useRef()
    const { isOpen, onOpen, onClose } = useDisclosure()
    const { popperRef, referenceRef } = usePopper()
    const { name, description } = props
    useOutsideClick({
        ref: outsideRef,
        handler: () => isOpen && onClose(),
    })

    const buttonEl = useMergeRefs(outsideRef, referenceRef)

    return (
        <>
            <button ref={buttonEl} onMouseEnter={onOpen} onMouseLeave={onClose}>
                {name}
            </button>
            {isOpen && (
                <Box ref={popperRef} bg='green'>
                    {description}
                </Box>
            )}
        </>
    )
}
