import { Container, Box, useDisclosure, ModalOverlay, Button, Modal, Text, ModalContent, ModalHeader, ModalCloseButton, ModalBody, ModalFooter, } from "@chakra-ui/react"
import React from "react"

export default function SignUp() {


    const OverlayOne = () => (
        <ModalOverlay
            bg='blackAlpha.300'
            backdropFilter='blur(10px) hue-rotate(90deg)'
        />
    )

    const { isOpen, onOpen, onClose } = useDisclosure()
    const [overlay, setOverlay] = React.useState(<OverlayOne />)

    return (
        <>
            <Button
                onClick={() => {
                    setOverlay(<OverlayOne />)
                    onOpen()
                }}
                fontSize={'x-small'}
                fontWeight={"500"}
                backgroundColor="whiteAlpha.100"
            >
                Sign Up/Sign In
            </Button>
            <Modal isCentered isOpen={isOpen} onClose={onClose} size={'2xl'}>
                {overlay}
                <ModalContent>
                    <Container maxW='2xl' bg='blue.400' centerContent>
                        <Box padding='4' bg='blue.400' color='black' maxW='md'>
                            <ModalHeader fontSize={'xx-large'}>Sign Up/Sign in</ModalHeader>
                            <Text fontSize={'xx-small'} paddingTop='-40px'>Enjoy the convenience of a single account across all participating brands</Text>
                        </Box>


                        <ModalCloseButton />
                        <ModalBody>

                        </ModalBody>
                        <ModalFooter>
                            <Button onClick={onClose}>Close</Button>
                        </ModalFooter>
                    </Container>
                </ModalContent>

            </Modal>
        </>
    )
}
